mapper.select:
select * from member 
where userid = #{ userid } and userpwd = #{ userpwd } 
and login_ok = 'Y'

select * from member

	where age >= #{ value }


	where age between #{ value } and #{ value } + 9

select count(userid) 
from member 
where userid = #{ uid }

mapper.update:
update member 
set 	userpwd = #{ userpwd }, 			
	age = #{ age }, 
	phone = #{ phone }, email = #{ email }, 
	hobby = #{ hobby }, etc = #{ etc } 
where userid = #{ userid }

update member 
set login_ok = #{ login_ok } 
where userid = #{ userid }
